import styled from "styled-components"

export const Container = styled.div`

h1{
  margin: 3rem 0;

}

.movie{
  display: flex;
  align-items: center;
  justify-content: center;
}

img {
  width: 300px;
  border-radius: 1rem;

}

.detalis{
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  margin-left: 4rem;
  max-width: 50%;

}

button{
  background: #6654da;
  border: none;
  cursor: pointer;
  border-radius: 1rem;
  color: white;
}
`
